      "template_uri": "author_html",
      "template_set": "author",
      "template_type": "author",
      "content_type": "html",
      "contents": "<!doctype html>\n<html lang=\"{{ global.website_language }}\">\n<head>\n  {{ global.head_code_snippets|raw }}\n  <meta charset=\"utf-8\" />\n  <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n  <meta name=\"viewport\" content=\"width=device-width, initial-scale=1.0\" />\n  <meta name=\"generator\" content=\"LibreBlog.org\" />\n  <link rel=\"schema.DC\" href=\"http://purl.org/dc/elements/1.1/\" />\n  <meta name=\"DC.Title\" content=\"{{ author.name|escape }}\" />\n  <meta name=\"DC.Description\" content=\"{{ author.bio|escape }}\" />\n  <meta property=\"og:title\" content=\"{{ author.name|escape }}\" />\n  <meta name=\"description\" content=\"{{ author.bio|escape }}\" />\n  <meta property=\"og:description\" content=\"{{ author.bio|escape }}\" />\n  {% if author.photo %}\n  <meta property=\"og:image\" content=\"{{ photo_version(author.photo, 'large') }}\" />\n  <meta property=\"og:image:alt\" content=\"{{ author.photo.alt_text }}\" />\n  {% endif %}\n  <meta property=\"og:type\" content=\"profile\" />\n  <meta property=\"profile:username\" content=\"{{ author.name|escape }}\" />\n  <meta property=\"og:url\" content=\"{{ global.website_url }}/authors/{{ author.uri }}{{ global.dot_html_for_links }}\" />\n  <title>{{ author.name|escape }}{% if global.website_name %}{{ ' | ' ~ global.website_name|escape }}{% endif %}</title>\n  <link rel=\"canonical\" href=\"{{ global.website_url }}/authors/{{ author.uri }}{{ global.dot_html_for_links }}\" />\n  {% for file in css_files %}\n  <link rel=\"stylesheet\" href=\"/css/{{ file }}\" />\n  {% endfor %}\n  {% if global.website_favicon %}\n  <link rel=\"icon\" type=\"image/x-icon\" href=\"{% if global.website_favicon|slice(0,7) == 'http://' or global.website_favicon|slice(0,8) == 'https://' %}{{ global.website_favicon }}{% else %}{{ media_url(global.website_favicon, media) }}{% endif %}\" />\n  {% endif %}\n  <script type=\"application/ld+json\">\n    {\n      \"@context\": \"https://schema.org\",\n      \"@type\": \"ProfilePage\",\n      \"dateCreated\": \"{{ iso_datetime(author.created) }}{{ global.website_timezone }}\",\n      \"dateModified\": \"{{ iso_datetime(author.updated) }}{{ global.website_timezone }}\",\n      \"mainEntity\": {\n        \"@id\": \"{{ global.website_url }}/authors/{{ author.uri }}{{ global.dot_html_for_links }}\",\n        \"@type\": \"Person\",\n        \"name\": \"{{ author.name|escape }}\",\n        \"description\": \"{{ author.bio|escape }}\",\n        \"image\": \"{{ photo_version(author.photo, 'large') }}\"\n      }\n    }\n  </script>\n</head>\n<body>\n  {{ components.header }}\n  <main class=\"an-column\">\n    <div id=\"author-card\" vocab=\"http://purl.org/dc/elements/1.1/\" typeof=\"dc:Resource\" resource=\"{{ global.website_url }}/authors/{{ author.uri }}{{ global.dot_html_for_links }}#author-card\">\n      {% if author.photo %}\n      <div id=\"author-image-div\">\n        <img id=\"author-image\" src=\"{{ photo_version(author.photo, 'small') }}\" alt=\"{{ author.photo.alt_text }}\" />\n      </div>\n      {% endif %}\n      <div id=\"author-contents\">\n        <div id=\"author-name\" role=\"heading\" aria-level=\"1\" property=\"dc:title\">{{ author.name|escape }}</div>\n        <div id=\"author-bio\" role=\"heading\" aria-level=\"2\" property=\"dc:description\">{{ author.bio }}</div>\n      </div>\n    </div>\n  </main>\n  <aside class=\"aside-column\">\n    {% if author.contents %}\n    <div id=\"contents-div\">\n      <div class=\"contents-wrapper\">\n        {{ author.contents|raw }}\n      </div>\n    </div>\n    {% endif %}\n    {% if author.contact or author.email %}\n    <div id=\"contact-div\">\n      <label id=\"contact-label\">{{ global.strings.contact }}</label>\n      {% if author.email %}\n      <div id=\"email-wrapper\">\n        <label id=\"email-label\">{{ global.strings.author_email }}:&nbsp;</label>\n        <span id=\"email-span\">{{ author.email }}</span>\n      </div>\n      {% endif %}\n      <div class=\"contents-wrapper\">{{ author.contact|raw }}</div>\n    </div>\n    {% endif %}\n    {% if articles %}\n    <div id=\"published-articles\" vocab=\"http://purl.org/dc/elements/1.1/\" typeof=\"dc:Collection\" resource=\"{{ global.website_url }}/authors/{{ author.uri }}{{ global.dot_html_for_links }}#published-articles\">\n      <label id=\"published-articles-label\" property=\"dc:type\">{{ global.strings.published_articles }}</label>\n      {% for article in articles %}\n      {% if article.in_sitemap %}\n      <article class='pb-article-card' typeof=\"dc:Resource\" resource=\"{{ global.website_url }}/articles/{{ article.uri }}{{ global.dot_html_for_links }}\">\n        {% if article.photo %}\n        <img class=\"pb-article-img\" src=\"{{ photo_version(article.photo, 'small') }}\" loading=\"lazy\" alt=\"{{ article.photo.alt_text }}\" />\n        {% endif %}\n        <div class=\"pb-article-contents\">\n          <div class=\"pb-article-title-div\" role=\"heading\" aria-level=\"2\">\n            <a class='pb-article-title' href='/articles/{{ article.uri }}{{ global.dot_html_for_links }}' property=\"dc:title\">{{ article.title|escape }}</a>\n          </div>\n          <div class=\"pb-article-description\" property=\"dc:description\">\n              {{ article.subtitle|raw }}\n          </div>\n        </div>\n      </article>\n      {% if not loop.last %}<div class=\"pa-divider\"><hr/></div>{% endif %}\n      {% endif %}\n      {% endfor %}\n    </div>\n    {% endif %}\n  </aside>\n  {% if how_many_pages > 1 %}\n  <div class=\"pagination-wrapper\">\n    <div class=\"pagination\">\n      {% if current_page > 1 %}\n      <a href=\"{% if current_page == 2 %}/authors/{{ author.uri }}{% else %}/authors/{{ author.uri }}/page/{{ current_page - 1 }}{% endif %}{{ global.dot_html_for_links }}\"><span>❮</span> {{ global.strings.previous_articles }}</a>\n      <span>⦁ ⦁ ⦁</span> \n      <a href=\"/authors/{{ author.uri }}{{ global.dot_html_for_links }}\">{{ global.strings.initial_page }}</a>\n      {% endif %}\n      {% if current_page < how_many_pages %}\n      {% if current_page > 1 %}<span>⦁ ⦁ ⦁</span>{% endif %}\n      <a href=\"/authors/{{ author.uri }}/page/{{ current_page + 1 }}{{ global.dot_html_for_links }}\">{{ global.strings.next_articles }} <span>❯</span></a>\n      {% endif %}\n    </div>\n  </div>\n  {% endif %}\n  {{ components.footer }}\n  <script src=\"/js/{{ search_js_file }}\" defer></script>\n  <script src=\"/js/main.js\"></script>\n</body>\n</html>\n"
